import React from "react";
import "../styles/pages/properties.scss";
import Navigation from "../components/Navigation";
import { NavLink } from "react-router-dom";
import Properties from "../components/Properties";
const PropertiesList = () => {
  const properties = [
    {
      img: "../assets/images/property_1-81a70efa.png",
      title: "Luxurious 3 BR home in Charlotte",
      desc: "Enjoy the luxury & comfort of this gorgeous 3 BR home. Our modern architecture provides stunning views of the golf course, while matching decor makes you feel at home every day. We've designed many elements of this house with you in mind to ensure you receive a comfortable and relaxing visit.",
      price: 119,
    },
    {
      img: "../assets/images/property_3-2cbdf1f8.png",
      title: "Bellyne 2BR Apt in Charlotte",
      desc: "Perfectly situated in the heart of Myers Park, this 2 BR apartment in a mid-century modern building is sure to please. The sunny kitchen overlooks the gorgeous living room and is outfitted with elegant furnishings. The large bedroom has ample closet space. It also features an onsite washer and dryer for your convenience.",
      price: 153,
    },
    {
      img: "../assets/images/property_2-36111017.png",
      title: "Ballantyne cottage 4 BR home",
      desc: "The stylish Ballantyne home is designed to provide a comfortable getaway to all visitors for a perfect stay. Its modern setting and outstanding facilities will offer the levels of relaxation and convenience you are looking for in a short-term stay.",
      price: 245,
    },
    {
      img: "../assets/images/property_4-1da07096.png",
      title: "Charlotte getaway 4 BR home",
      desc: "Imagine walking into a beautifully decorated home straight out of a magazine! All your troubles melt away & you breathe a sigh of relief. This newly renovated 4-bedroom, 3 bath, split-level home situated in a quiet, Ballantyne neighborhood. Spacious but cozy, private & relaxing. Close to the airport, Uptown & everything Charlotte has to offer. Perfect for couples, business travelers, and families.",
      price: 275,
    },
  ];
  const affiche = properties.map((property, index) => {
    return <Properties key={index} />;
  });
  return (
    <div>
      <div class="container">
        <div className="main">
          <Navigation />
          <div className="titre border-bottom">
            <h1>Our properties</h1>
          </div>
          <div>{affiche}</div>
          <div className="contact border-bottom">
            <NavLink to="">
              <h1>Contact us now</h1>
            </NavLink>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PropertiesList;
