import React from "react";
import Navigation from "../components/Navigation";
// import "../styles/pages/home.scss";

const Home = () => {
  const [moviesUrlBackgroundColor, setMoviesUrlBackgroundColor] =
    useState("green");
  const [bgFlag, setbgFlag] = useState(false);

  const getMoviesUrl = window.location.pathname;

  if (!bgFlag) {
    setMoviesUrlBackgroundColor(getMoviesUrl == "/movies" ? "red" : "green");
    setbgFlag(true);
  }
  return (
    <div
      class="container"
      style={{ backgroundColor: moviesUrlBackgroundColor }}
    >
      <div className="main">
        <Navigation />
        <div class="step">
          <div>
            <p>Welcome to</p>
          </div>
          <div>
            <h1>E-smart rentals charlotte</h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
