import React, { useState } from "react";
import Navigation from "../components/Navigation";
// import "../styles/pages/home.scss";

const Home = () => {
  const [moviesUrlBackground, setMoviesUrlBackground] = useState("green");
  const [bgFlag, setbgFlag] = useState(false);

  const getMoviesUrl = window.location.pathname;

  if (!bgFlag) {
    setMoviesUrlBackground(
      getMoviesUrl == "/movies" ? "red" : "url(../assets/images/home_bg.png)"
    );
    setbgFlag(true);
  }
  return (
    <div class="container" style={{ background: moviesUrlBackground }}>
      <div className="main">
        <Navigation />
        <div class="step">
          <div>
            <p>Welcome to</p>
          </div>
          <div>
            <h1>E-smart rentals charlotte</h1>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
